import React from 'react';
import { render, screen } from '@testing-library/react';
import '@testing-library/jest-dom/extend-expect';
import GameInfo from '../../../components/game-info/GameInfo';

describe('GameInfo Component', () => {
  test('renders player X turn message when xIsNext is true and no winner', () => {
    render(<GameInfo xIsNext={true} winner={null} />);
    expect(screen.getByText("It's your turn, player X")).toBeInTheDocument();
  });

  test('renders player O turn message when xIsNext is false and no winner', () => {
    render(<GameInfo xIsNext={false} winner={null} />);
    expect(screen.getByText("Now you, player O!")).toBeInTheDocument();
  });

  test('renders player X winner message when winner is X', () => {
    render(<GameInfo winner="X" status="Winner: X" />);
    expect(screen.getByText("Nice! I won!")).toBeInTheDocument();
  });

  test('renders player O winner message when winner is O', () => {
    render(<GameInfo winner="O" status="Winner: O" />);
    expect(screen.getByText("Wohoo! I made it!")).toBeInTheDocument();
  });

  test('renders the friends image', () => {
    render(<GameInfo xIsNext={true} winner={null} />);
    const image = screen.getByAltText('Player X and Player O');
    expect(image).toBeInTheDocument();
    expect(image).toHaveAttribute('src', expect.stringContaining('friends.webp'));
  });

  // Additional test cases can be added here for edge cases and error handling if needed.
});

